import React, { useState } from 'react';
import ParticlesBg from 'particles-bg';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import './App.css';
import ParticlesBackground from './components/ParticlesBackground.js';
import { CircularProgress } from '@mui/material';
import OpenAI from 'openai';

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_API_KEY,
  dangerouslyAllowBrowser: true
});

const App = () => {
  const [input, setInput] = useState('');
  const [route, setRoute] = useState('signin');
  const [isSignedIn, setIsSignedIn] = useState(false);
  const [user, setUser] = useState({
    id: '',
    name: '',
    email: '',
    entries: 0,
    joined: ''
  });
  const [generatedImageUrl, setGeneratedImageUrl] = useState('');
  const [prompt, setPrompt] = useState('');
  const [url, setUrl] = useState('');
  const [loading, setLoading] = useState(false);

  const   onButtonSubmit= async () => {
    setLoading(true);
    const response = await openai.images.generate({
      model: "dall-e-3",
      prompt:  input,
      n: 1,
      size: "1024x1024",
    });
    let image_url = response.data[0].url;
    setUrl(image_url);
    setLoading(false);
  }

  const loadUser = (data) => {
    setUser({
      id: data.id,
      name: data.name,
      email: data.email,
      entries: data.entries,
      joined: data.joined
    });
  }

  const onInputChange = (event) => {
    setInput(event.target.value);
  }

  const onRouteChange = (route) => {
    if (route === 'signout') {
      setIsSignedIn(false);
    } else if (route === 'home') {
      setIsSignedIn(true);
    }
    setRoute(route);
  }

  return (
    <div className="App">
      <ParticlesBackground id='ParticlesBackground' />
      <Navigation isSignedIn={isSignedIn} onRouteChange={onRouteChange} />
      {route === 'home' ? (
        <div>
          <Rank
            name={user.name}
            entries={user.entries}
          />
          <ImageLinkForm
            onInputChange={onInputChange}
            onButtonSubmit={  onButtonSubmit}
          />
          {!loading && url !== "" &&
            <img src={url} style={{ height: '500px', width: '500px', position: 'relative',
            top: '100px', }} alt="Generated" />
          }
          {loading &&
            <CircularProgress style={{position: 'relative',
            top: '125px' }}> </CircularProgress>
          }
        </div>
      ) : (
        route === 'signin' ? <Signin loadUser={loadUser} onRouteChange={onRouteChange} /> : <Register loadUser={loadUser} onRouteChange={onRouteChange} />
      )}
    </div>
  );
}

export default App;
