import React, { Component } from 'react';
import ParticlesBg from 'particles-bg';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin';
import Register from './components/Register/Register';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import './App.css';
import ParticlesBackground from './components/ParticlesBackground.js';

const app = new Clarifai.App({
  apiKey: 'e51efb33f6234d068f3035c18a2bdc13'
});
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      route: 'signin',
      isSignedIn: false,
      user: {
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
      },
      generatedImageUrl: ''
    }
  }

  loadUser = (data) => {
    this.setState({
      user: {
        id: data.id,
        name: data.name,
        email: data.email,
        entries: data.entries,
        joined: data.joined
      }
    });
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  }

  onButtonSubmit = () => {
    const { input } = this.state;
    const PAT = 'b2b7fbb32a354800b86c3ac23cbedc09'; // Your PAT
    const MODEL_ID = 'stable-diffusion-xl';
    const MODEL_VERSION_ID = '68eeab068a5e4488a685fc67bc7ba71e';


    const raw = JSON.stringify({  
      "inputs": [
        {
          "data": {
            "text": {
              "raw": input
            }
          }
        }
      ]
    });

    const requestOptions = {
      method: 'POST',
      headers: {

        'Accept': 'application/json',
        'Authorization': 'Key ' + PAT,
        'USER_ID' : 'stability-ai',
        'APP_ID': 'stable-diffusion-2',
      },
      body: raw
    };

    fetch("https://api.clarifai.com/v2/models/" + MODEL_ID + "/versions/" + MODEL_VERSION_ID + "/outputs", requestOptions)
      .then(response => response.json())
      .then(result => {
        const imageBase64 = result.outputs[0].data.image.base64;
        this.setState({ generatedImageUrl: `data:image/jpeg;base64,${imageBase64}` });
      })      
      .catch(error => console.log('error', error));
  }


  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState({ isSignedIn: false })
    } else if (route === 'home') {
      this.setState({ isSignedIn: true })
    }
    this.setState({ route: route });
  }


  render() {
    const { isSignedIn, route, generatedImageUrl  } = this.state;
    return (
      <div className="App">
        <ParticlesBackground id='ParticlesBackground' />
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange} />
        {route === 'home'
          ? <div>
              <Rank
                name={this.state.user.name}
                entries={this.state.user.entries}
              />
              <ImageLinkForm
                onInputChange={this.onInputChange}
                onButtonSubmit={this.onButtonSubmit}
              />
              {generatedImageUrl && <img src={generatedImageUrl} alt="Generated" />}
            </div>
          : (
            route === 'signin'
              ? <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange} />
              : <Register loadUser={this.loadUser} onRouteChange={this.onRouteChange} />
          )
        }
      </div>
    );
  }
}

export default App;
